	public ConnectedThread(BluetoothSocket socket, BluetoothDevice device, BluetoothChatService btChatService) {
		//


		this.btChatService = btChatService;
		Log.d(TAG, "create ConnectedThread");
		mmSocket = socket;
		InputStream tmpIn = null;
		OutputStream tmpOut = null;
		this.setDevice(device);
		// Get the BluetoothSocket input and output streams
		try {
			if (socket != null) {
				tmpIn = socket.getInputStream();
				tmpOut = socket.getOutputStream();
			} else {
				System.out.println("Socket is closed by the server");
			}
		} catch (IOException e) {
			Log.e(TAG, "temp sockets not created", e);
		}
		mmInStream = tmpIn;
		mmOutStream = tmpOut;
	}

	// To reset printer for finger print scan
	public void FPReset() {
		byte[] reset = new byte[8];
		reset[0] = (byte) 0x7e;
		reset[1] = (byte) 0xb2;
		reset[2] = (byte) 0xf0;
		reset[3] = (byte) 0x41;
		reset[4] = (byte) 0x42;
		reset[5] = (byte) 0x04;
		reset[6] = (byte) 0x45;

		try {
			Thread.sleep(2000);
			mmOutStream.write(reset);
			mmOutStream.flush();
			Thread.sleep(2000);
		} catch (Exception e) {
			e.printStackTrace();
			Log.d(TAG, "IO Error");
		}
	}









