	public int PrinterData(String data, int Font, boolean mAlign) {
		if (mAlign) {
			int space = 0;
			if (Font == 1 || Font == 2) {
				if (data.length() < 25) {
					space = 24 - data.length();
					for (int i = 0; i < space; i++) {
						if (i < (space / 2)) {
							data = " " + data;
						} else {
							data = data + " ";
						}
					}
				}
			} else if (Font == 3 || Font == 4) {
				if (data.length() < 42) {
					space = 42 - data.length();
					for (int i = 0; i < space; i++) {
						if (i < (space / 2)) {
							data = " " + data;
						} else {
							data = data + " ";
						}
					}
				}
			}
		}
		System.out.println("data:" + data + "datalength:" + data.length());
		int i = 0, j = 0, col = 0, la = 0;
		char[] tempBuffer = new char[1000];
		int[] dataBuffer = new int[5000];
		byte[] b = new byte[2];
		System.out.println(data);
		data.getChars(0, data.length(), tempBuffer, col);
		// int totDataLen = data.length();
		/////////////////////// PACKET FORMAT /////////////////////
		for (i = 0; la < data.length();) {
			if (Font == 1 || Font == 2) {
				if (Font == 1) {
					dataBuffer[i++] = 0xf0;
				} else if (Font == 2) {
					dataBuffer[i++] = 0xf1;
				}
				for (j = 0; j < 24; j++) {
					if (la < data.length()) {
						dataBuffer[i++] = tempBuffer[la++];
					} else {
						break;
					}
				}
			} else if (Font == 3 || Font == 4) {
				if (Font == 3) {
					dataBuffer[i++] = 0xf2;
				} else if (Font == 4) {
					dataBuffer[i++] = 0xf3;
				}
				for (j = 0; j < 42; j++) {
					if (la < data.length()) {
						dataBuffer[i++] = tempBuffer[la++];
					} else {
						break;
					}
				}
			}
		}
		/////////////////////// PACKET FORMAT /////////////////////
		try {
			SendPrint(dataBuffer, i, Font);
		} catch (IOException ex) {

		}
		return 1;
	}

