	public int SendPrint(int[] pData, int pLength, int Font) throws IOException {
		int[] pBuff = new int[1000];
		int n, lrc = 0, space = 0, i = 0, iBreak = 0, dataPack = 0, pPos = 0, pInc = 0;
		int iEvLength = 0;

		if (Font == 1 || Font == 2) {
			dataPack = pLength / 125 + 1;
		} else if (Font == 3 || Font == 4) {
			dataPack = pLength / 86 + 1;
		}

		System.out.println(dataPack);
		if (Font == 1 || Font == 2) {
			space = pLength % 125;
		} else if (Font == 3 || Font == 4) {
			space = pLength % 86;
		}
		/////////////////// APPENDING SPACES///////////////////
		if (Font == 1 || Font == 2) {
			for (i = 0;; i++) {
				iBreak = space % 25;
				if (iBreak == 0) {
					break;
				}
				pData[pLength++] = 0x20;
				space++;
			}
		} else if (Font == 3 || Font == 4) {
			for (i = 0;; i++) {
				iBreak = space % 43;
				if (iBreak == 0) {
					break;
				}
				pData[pLength++] = 0x20;
				space++;
			}
		}
		/////////////////// APPENDING SPACES///////////////////
		while (dataPack > 0) {
			pBuff[0] = 0x7e;
			pBuff[1] = 0xb2;
			if (Font == 1 || Font == 2) {
				pPos = pInc * 125;
				if (dataPack > 1) {
					iEvLength = 125;
				} else {
					iEvLength = pLength - pPos;
				}
			} else if (Font == 3 || Font == 4) {
				pPos = pInc * 86;
				if (dataPack > 1) {
					iEvLength = 86;
				} else {
					iEvLength = pLength - pPos;
				}
			}
			System.out.println("iEvLength=" + iEvLength + "Pos=" + pPos);
			System.arraycopy(pData, pPos, pBuff, 2, iEvLength);
			iEvLength = iEvLength + 2;
			pBuff[iEvLength++] = 0x04;

			for (n = 1; n < iEvLength; n++) {
				lrc ^= pBuff[n];
			}
			pBuff[iEvLength++] = lrc;
			try {
				for (k = 0; k < iEvLength; k++) {
					mmOutStream.write(pBuff[k]);
				}
				mmOutStream.flush();
				////sss
				//ReadPrinterresp();
			} catch (IOException ex) {
				ex.printStackTrace();
			}

			dataPack--;
			pInc++;
			lrc = 0;
		}
		return 1;
	}

