	private void printText(byte[] msg) {

		Log.v("","printText");
		try {
			// Print normal text
			outputStream.write(msg);
			printNewLine();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
	private void printNewLine() {
		try {
			outputStream.write(PrinterCommands.FEED_LINE);
		} catch (IOException e) {
			e.printStackTrace();
		}

	}
